import React from "react";
import "./App.css";
import TextField from "@mui/material/TextField";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlus,
  faTrash,
  faPenToSquare,
  faCircleCheck,
} from "@fortawesome/free-solid-svg-icons";
import Button from "@mui/material/Button";
import swal from "sweetalert";

function App() {
  const [getinput, Setinput] = useState("");
  const [data, Setdata] = useState([]);
  const [edit, setedit] = useState(false);
  const [updated, setupdated] = useState("");

  const Additems = () => {
    if (getinput === "") {
      swal("Sorry!", "Please Enter The Input Value First", "error");
    } else {
      Setdata([...data, { items: getinput }]);
      Setinput("");
    }
  };

  const deleteitem = (indexnumber) => {
    console.log(indexnumber);
    const arr = data;
    arr.splice(indexnumber, 1);
    Setdata([...arr]);
  };
  const deleteall = () => {
    Setdata([]);
  };

  const handleEdit = (value) => {
console.log(value)
Setinput(value.items)
  }

  return (
    <>
      <div className="header">
        <TextField
          id="standard-basic"
          value={getinput}
          onChange={(e) => Setinput(e.target.value)}
          label="Enter Items"
          variant="standard"
        />
        <FontAwesomeIcon className="addicon" onClick={Additems} icon={faPlus} />
      </div>
      <div className="todoitems">
        {data.map((value, index) => {
          return (
            <div className="eachitems" key={index}>
              <span>{value.items}</span>
              <div>
                <FontAwesomeIcon
                  className="Deleteicon"
                  onClick={() => {
                    deleteitem(index);
                  }}
                  icon={faTrash}
                  beatFade
                />
                <FontAwesomeIcon className="Done" shake icon={faCircleCheck} />
                <FontAwesomeIcon className="Edit" onClick={()=>{handleEdit(value)}} shake icon={faPenToSquare} />
              </div>
            </div>
          );
        })}
        <Button onClick={deleteall} variant="outlined" color="error">
          Delete All
        </Button>
      </div>
    </>
  );
}

export default App;
