import React from "react";
import "./App.css";
import TextField from "@mui/material/TextField";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlus,
  faTrash,
  faPenToSquare,
  faCircleCheck,
} from "@fortawesome/free-solid-svg-icons";
import Button from "@mui/material/Button";
import swal from "sweetalert";
// import { isDisabled } from "@testing-library/user-event/dist/utils";

function App() {
  const [getinput, Setinput] = useState("");
  const [data, Setdata] = useState([]);
  const [edit, setedit] = useState("");
  // const [done, setdone] = useState(false);
  const [check, setcheck] = useState("")
  const [checkval, setcheckval] =useState("")

  const Additems = () => {

    if (getinput === "") {
      swal("Sorry!", "Please Enter The Input Value First", "error");
    } else {
      Setdata([...data, { items: getinput}]);
      Setinput("");
    }
    // Setdata([...data, {items: edit}])
  };

  const deleteitem = (indexnumber) => {
    // console.log(indexnumber);
    const arr = data;
    arr.splice(indexnumber, 1);
    Setdata([...arr]);
  };


  const deleteall = () => {
    Setdata([]);
  };

  const handleEdit = (value, index) => {
    setedit(value.items);
    Setinput(value.items);
    const editarr = data;
    editarr.splice(value, 1);
    Setdata([...editarr]);
  };

  const handleDone = (value) => {
    const val = value
    setcheckval(val);
    setcheck("checked")
  };

  return (
    <>
      <div className="header">
        <TextField
          id="standard-basic"
          value={getinput}
          onChange={(e) => Setinput(e.target.value)}
          label="Enter Iytems"
          variant="standard"
        />
        <FontAwesomeIcon className="addicon" onClick={Additems} icon={faPlus} />
      </div>
      <div className="todoitems">
        {data.map((value, index) => {
          return (
            <div className="eachitems" key={index}>
              <span>{value.items}</span>
              <div>
                <FontAwesomeIcon
                  className="Deleteicon"
                  onClick={() => {
                    deleteitem(index);
                  }}
                  icon={faTrash}
                  beatFade
                />
                <FontAwesomeIcon
                  className="Done"
                  class={check}
                  disabled={done}
                  onClick={()=>
                    {handleDone(value)}
                  }
                  shake
                  icon={faCircleCheck}
                />
                <FontAwesomeIcon
                  className="Edit"
                  onClick={() => {
                    handleEdit(value, index);
                  }}
                  shake
                  icon={faPenToSquare}
                />
              </div>
            </div>
          );
        })}
        <Button onClick={deleteall} variant="outlined" color="error">
          Delete All
        </Button>
      </div>
    </>
  );
}

export default App;
